import { useNavigate } from "react-router-dom";
import MyButton from "../components/MyButton";
import MyHeader from "../components/MyHeader";
import DiaryList from "../components/DiaryList";

import { useContext, useEffect, useState } from "react";
import { DiaryStateContext } from "../App";

const Home = () => {
  //ÏùºÍ∏∞ Î¶¨Ïä§Ìä∏ Í¥ÄÎ¶¨------
  const diaryList = useContext(DiaryStateContext);
  const [data, setData] = useState([]);
  const [monthlyReview, setMonthlyReview] = useState("");
  const [monthlyComment, setMonthlyComment] = useState("");

  //ÎÇ†Ïßú Í¥ÄÎ¶¨ (Ïõî, ÎÖÑ)------
  const [curDate, setCurDate] = useState(new Date()); //Í∏∞Î≥∏Í∞í: ÌòÑÏû¨ ÏãúÍ∞Ñ

  const onIncrease = () => {
    setCurDate(
      new Date(curDate.getFullYear(), curDate.getMonth() + 1, curDate.getDate())
    );
  };

  const onDecrease = () => {
    setCurDate(
      new Date(curDate.getFullYear(), curDate.getMonth() - 1, curDate.getDate())
    );
  };

  const reviewEmotion = (diaryList) => {
    let count = 0;
    diaryList.map((it) => {
      if (it.emotion <= 3) count++;
    });

    if (count / diaryList.length >= 0.7) {
      setMonthlyReview(" Í∏∞Î∂Ñ Ï¢ãÏùÄ");
      console.log(curDate.getMonth() + 1);
      setMonthlyComment("Ïù¥ÎåÄÎ°úÎßå ÌñâÎ≥µÌïòÏÑ∏Ïöî! ü•∞");
    } else if (count / diaryList.length >= 0.5) {
      setMonthlyReview(" Í∑∏Îü≠Ï†ÄÎü≠Ìïú");
      setMonthlyComment("Ï°∞Í∏à Îçî Í∏çÏ†ïÏ†ÅÏúºÎ°ú ÏÉùÍ∞ÅÌï¥Î¥êÏöî! üí™");
    } else {
      setMonthlyReview(" Ï°∞Í∏àÏùÄ ÌûòÎì† ");
      setMonthlyComment("Ïù¥Ï†† Ï¢ãÏùÄ ÏùºÏù¥ ÏÉùÍ∏∏ Í±∞ÏòàÏöî! üçÄ");
    }
  };

  const headText = `${curDate.getFullYear()}ÎÖÑ ${curDate.getMonth() + 1}Ïõî`;

  //ÎÇ†ÏßúÏóê Îî∞Î•∏ Îç∞Ïù¥ÌÑ∞ Í¥ÄÎ¶¨
  useEffect(() => {
    if (diaryList.length >= 1) {
      const firstDay = new Date(
        curDate.getFullYear(),
        curDate.getMonth(),
        1
      ).getTime();

      const lastDay = new Date(
        curDate.getFullYear(),
        curDate.getMonth() + 1,
        0,
        23,
        59,
        59
      ).getTime();

      setData(
        diaryList.filter((it) => firstDay <= it.date && it.date < lastDay)
      );

      reviewEmotion(diaryList);
    }
  }, [diaryList, curDate]);

  useEffect(() => {}, [data]);

  return (
    <div>
      <MyHeader
        leftChild={<MyButton text={"<"} onClick={onDecrease}></MyButton>}
        headText={headText}
        rightChild={<MyButton onClick={onIncrease} text={">"}></MyButton>}
      ></MyHeader>
      <DiaryList diaryList={data}></DiaryList>
      <div className="monthlyEmotion">
        <h3>
          <span>
            {monthlyReview}
            {" " + (curDate.getMonth() + 1) + "Ïõî"}
          </span>
          Ïù¥ÎÑ§Ïöî!
        </h3>
        <br></br>
        <div className="monthReview">{monthlyComment}</div>
      </div>
    </div>
  );
};

export default Home;
