import { useState } from "react"


//필터링을 위한 ControlMenu
//value: 어떤 게 선택된 상태인가? onChange: 다른 걸 선택하면 어떻게 할 것인가?
//optionList: 어떤 option이 있는가?
const ControlMenu = ({value, onChange, optionList}) => {
    return <select value={value} onChange={(e)=>{
        onChange(e.target.value)
    }}>
        {optionList.map((it)=>(<option value={it.value}>{it.name}</option>
            ))}
    </select>
}

const sortOptionList = [
    {value:"latest", name:"최신순"},
    {value:"oldest", name:"오래된 순"},
]

const emotionOptionList = [
    {value:"all", name:"전부 다"},
    {value:"good", name:"좋은 감정만"},
    {value:"bad", name:"안 좋은 감정만"},
]

const DiaryList = ({diaryList}) => {

    const [sortType, setSortType] = useState('latest');

    const [emotionType, setEmotionType] = useState('all');

    console.log({diaryList});
    return (
    <div>
        <ControlMenu value={sortType} onChange={setSortType} optionList={sortOptionList}></ControlMenu>
        <ControlMenu value={emotionType} onChange={setEmotionType} optionList={emotionOptionList}></ControlMenu>
        {diaryList.map((it)=>(
            <div key={it.id}>{it.content}</div>
        ))}
    </div>)
}

//디폴트 props를 빈 배열로 전달
DiaryList.defaultProps = {
    diaryList : [],
};

export default DiaryList;
