import MyButton from "../components/MyButton";
import MyHeader from "../components/MyHeader";

import { useState } from "react";

const Home = () => {

    const [curDate, setCurDate] = useState(new Date());

    const headText = `${curDate.getFullYear()}년 ${curDate.getMonth()+1}월`;

    return(
        <div>
        <MyHeader leftChild={<MyButton text={"<"}></MyButton>} headText={headText} rightChild={<MyButton text={">"}></MyButton>}></MyHeader>
        <div className="menu_wrapper">
            <div className="left_Col">
                <select className="ControlMenu">
                    <option value={"latest"}>최신순</option>
                    <option value={"oldest"}>오래된 순</option>
                </select>
                <select className="ControlMenu">
                    <option value={"all"}>전부 다</option>
                    <option value={"good"}>좋은 감정만</option>
                    <option value={"bad"}>안 좋은 감정만</option>
                </select>
            </div>
            <div className="right_Col">
                <button className="MyButton MyButton_positive">새 일기 쓰기</button>
            </div>
        </div>
        </div>
    )
}

export default Home;
