import { useContext, useState } from "react";
import MyButton from "./MyButton";
import { DiaryDispatchContext } from "../App";

const DiaryEditor = () => {
    //날짜 선택 관리
    const [date, setDate] = useState(new Date().toISOString().slice(0,10));

    //감정 선택 관리
    const [emotion, setEmotion] = useState(3);  

    //본문 작성 관리
    const [content, setContent] = useState("");

    //context로부터 onCreate 함수를 받아옴
    const onCreate = useContext(DiaryDispatchContext).onCreate;

    const AddNewDiary = () => {
        const newDate= new Date(date).getSeconds();
        console.log(newDate);
        onCreate({emotion, content, date: newDate});
        setDate(new Date().toISOString().slice(0,10));
        setEmotion(3);
        setContent("");
    };

    return(
        <div className="DiaryEditor">
            <div>
                <section>
                    <h4>오늘은 언제인가요?</h4>
                    <div className="input_box">
                        <input className="input_date" type="date" value={date} onChange={(e)=>{setDate(e.target.value)}}></input>
                    </div>
                </section>
                <section>
                    <h4>오늘의 감정</h4>
                    <div className="input_box emotion_list_wrapper">
                        <div>{emotion}</div>
                    </div>
                </section>
                <section>
                    <h4>오늘의 일기</h4>
                    <div className="input_box text_wrapper">
                        <textarea placeholder="오늘은 어땠남" value={content} onChange={(e)=>(setContent(e.target.value))}></textarea>
                    </div>
                </section>
                <section>
                    <div className="control_box">
                        <MyButton text={"취소하기"} type={"default"}></MyButton>
                        <MyButton text={"작성 완료"} type={"positive"} onClick={AddNewDiary}></MyButton>
                    </div>
                </section>
            </div>
        </div>
    )
}

export default DiaryEditor;
