import { useContext, useRef, useState } from "react";
import MyButton from "./MyButton";
import { DiaryDispatchContext } from "../App";
import { useNavigate } from "react-router-dom";
import EmotionItem from "./EmotionItem";

const env = process.env;
env.PUBLIC_URL = env.PUBLIC_URL || "";

let emotionList = [
  {
    emotion_id: 1,
    emotion_img: process.env.PUBLIC_URL + `/assets/emotion1.png`,
    emotion_discript: "완전 좋음",
  },
  {
    emotion_id: 2,
    emotion_img: process.env.PUBLIC_URL + `/assets/emotion2.png`,
    emotion_discript: "좋음",
  },
  {
    emotion_id: 3,
    emotion_img: process.env.PUBLIC_URL + `/assets/emotion3.png`,
    emotion_discript: "그럭저럭",
  },
  {
    emotion_id: 4,
    emotion_img: process.env.PUBLIC_URL + `/assets/emotion4.png`,
    emotion_discript: "나쁨",
  },
  {
    emotion_id: 5,
    emotion_img: process.env.PUBLIC_URL + `/assets/emotion5.png`,
    emotion_discript: "끔찍함",
  },
];

const DiaryEditor = ({ originData }) => {
  const navigate = useNavigate();

  //날짜 선택 관리
  const [date, setDate] = useState(
    isEdit ? originData.date : new Date().toISOString().slice(0, 10)
  );

  //감정 선택 관리
  const [emotion, setEmotion] = useState(isEdit ? originData.emotion : 3);

  const handleClickEmotion = (emotion) => {
    setEmotion(emotion);
  };

  //본문 작성 관리
  const [content, setContent] = useState(isEdit ? originData.content : "");

  const contentInput = useRef();

  //context로부터 onCreate 함수를 받아옴
  const onCreate = useContext(DiaryDispatchContext).onCreate;

  const AddNewDiary = () => {
    if (content.length < 1) {
      contentInput.current.focus();
      return;
    }
    if (window.confirm("새로운 일기를 작성하시겠습니까?")) {
      const newDate = new Date(date).getTime();
      onCreate({ emotion, content, date: newDate });
      setDate(new Date().toISOString().slice(0, 10));
      setEmotion(3);
      setContent("");
      navigate("/", { replace: true });
    }
  };

  return (
    <div className="DiaryEditor">
      <div>
        <section>
          <h4>오늘은 언제인가요?</h4>
          <div className="input_box">
            <input
              className="input_date"
              type="date"
              value={date}
              onChange={(e) => {
                setDate(e.target.value);
              }}
            ></input>
          </div>
        </section>
        <section>
          <h4>오늘의 감정</h4>
          <div className="input_box emotion_list_wrapper">
            {emotionList.map((it) => (
              <EmotionItem
                onClick={handleClickEmotion}
                key={it.emotion_id}
                {...it}
                isSelected={it.emotion_id === emotion}
              ></EmotionItem>
            ))}
          </div>
        </section>
        <section>
          <h4>오늘의 일기</h4>
          <div className="input_box text_wrapper">
            <textarea
              ref={contentInput}
              placeholder="오늘은 어땠남"
              value={content}
              onChange={(e) => {
                contentInput.current.focus();
                setContent(e.target.value);
              }}
            ></textarea>
          </div>
        </section>
        <section>
          <div className="control_box">
            <MyButton
              text={"취소하기"}
              type={"default"}
              onClick={() => {
                navigate(-1);
              }}
            ></MyButton>
            <MyButton
              text={"작성 완료"}
              type={"positive"}
              onClick={AddNewDiary}
            ></MyButton>
          </div>
        </section>
      </div>
    </div>
  );
};

export default DiaryEditor;
